import axios from 'axios';

const API_BASE_URL = 'http://localhost:8083/api';

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Types for API requests and responses
export interface Artist {
  artist_id: string;
  artist_name: string;
}

export interface Venue {
  venue_id: string;
  venue_name: string;
  city: string;
  capacity: number;
}

export interface Tour {
  tour_id: string;
  tour_name: string;
  artist_id: string;
}

export interface Concert {
  concert_id: string;
  concert_date: string;
  tour_id: string;
  venue_id: string;
  venue_name?: string;
  city?: string;
  capacity?: number;
  tour_name?: string;
  artist_name?: string;
}

export interface WeeklySales {
  sale_id: string;
  concert_id: string;
  calendar_week: string;
  sales_week_number: number;
  tickets_sold_in_week: number;
  tickets_sold_total: number;
}

export interface CreateArtistRequest {
  artist_name: string;
}

export interface CreateVenueRequest {
  venue_name: string;
  city: string;
  capacity: number;
}

export interface CreateTourRequest {
  tour_name: string;
  artist_id: string;
}

export interface CreateConcertRequest {
  concert_date: string;
  tour_id: string;
  venue_id: string;
}

export interface CreateWeeklySalesRequest {
  concert_id: string;
  calendar_week: string;
  sales_week_number: number;
  tickets_sold_in_week: number;
  tickets_sold_total: number;
}

export interface UpdateWeeklySalesRequest {
  tickets_sold_in_week: number;
  tickets_sold_total: number;
}

export interface BulkWeeklySalesRequest {
  entries: Array<{
    concert_id: string;
    calendar_week: string;
    sales_week_number: number;
    tickets_sold_in_week: number;
    tickets_sold_total: number;
  }>;
}

export interface CurrentWeekSalesResponse {
  current_week: number;
  calendar_week: string;
  sales: WeeklySales[];
}

// Data Entry API Functions

export const dataEntryApi = {
  // Get data
  getVenues: async (): Promise<{ venues: Venue[] }> => {
    const response = await api.get('/venues');
    return response.data;
  },

  getTours: async (): Promise<{ tours: Tour[] }> => {
    const response = await api.get('/tours');
    return response.data;
  },

  getConcerts: async (filters?: { artist_id?: string; tour_id?: string }): Promise<{ concerts: Concert[] }> => {
    const params = new URLSearchParams();
    if (filters?.artist_id) params.append('artist_id', filters.artist_id);
    if (filters?.tour_id) params.append('tour_id', filters.tour_id);

    const response = await api.get(`/concerts?${params.toString()}`);
    return response.data;
  },

  getCurrentWeekSales: async (): Promise<CurrentWeekSalesResponse> => {
    const response = await api.get('/sales/current-week');
    return response.data;
  },

  getSalesTrends: async (artist?: string): Promise<any> => {
    const params = artist ? { artist } : {};
    const response = await api.get('/sales-trends', { params });
    return response.data;
  },

  getSalesByWeek: async (weekNumber: number): Promise<{ week: number; calendar_week: string; sales: WeeklySales[] }> => {
    const response = await api.get(`/sales/week/${weekNumber}`);
    return response.data;
  },

  getCurrentVerkaufswoche: async (): Promise<{
    current_sales_week: number;
    previous_sales_week: number | null;
    available_sales_weeks: number[];
    sales: WeeklySales[];
    previous_week_available: boolean;
  }> => {
    const response = await api.get('/sales/current-verkaufswoche');
    return response.data;
  },

  getSalesByVerkaufswoche: async (salesWeek: number): Promise<{ sales_week: number; sales: WeeklySales[] }> => {
    const response = await api.get(`/sales/verkaufswoche/${salesWeek}`);
    return response.data;
  },

  getArtists: async (): Promise<{ artists: Artist[] }> => {
    const response = await api.get('/artists');
    const artistNames = response.data.artists as string[];

    // Convert string array to Artist array
    const artists: Artist[] = artistNames.map((name, index) => ({
      artist_id: `artist_${index + 1}`,
      artist_name: name
    }));

    return { artists };
  },

  // Create operations
  createArtist: async (data: CreateArtistRequest): Promise<{ message: string; artist_id: string; artist_name: string }> => {
    const response = await api.post('/artists', data);
    return response.data;
  },

  createVenue: async (data: CreateVenueRequest): Promise<{ message: string; venue_id: string; venue_name: string; city: string; capacity: number }> => {
    const response = await api.post('/venues', data);
    return response.data;
  },

  createTour: async (data: CreateTourRequest): Promise<{ message: string; tour_id: string; tour_name: string; artist_id: string }> => {
    const response = await api.post('/tours', data);
    return response.data;
  },

  createConcert: async (data: CreateConcertRequest): Promise<{ message: string; concert_id: string; concert_date: string; tour_id: string; venue_id: string }> => {
    const response = await api.post('/concerts', data);
    return response.data;
  },

  createWeeklySales: async (data: CreateWeeklySalesRequest): Promise<{ message: string; sale_id: string; concert_id: string; calendar_week: string; tickets_sold_in_week: number; tickets_sold_total: number }> => {
    const response = await api.post('/sales/weekly', data);
    return response.data;
  },

  // Update operations
  updateWeeklySales: async (saleId: string, data: UpdateWeeklySalesRequest): Promise<{ message: string; sale_id: string; tickets_sold_in_week: number; tickets_sold_total: number }> => {
    const response = await api.put(`/sales/${saleId}`, data);
    return response.data;
  },

  // Bulk operations
  bulkCreateWeeklySales: async (data: BulkWeeklySalesRequest): Promise<{ message: string; successful_entries: number; errors: string[]; results: Array<{ entry_index: number; sale_id: string; status: string }> }> => {
    const response = await api.post('/sales/bulk', data);
    return response.data;
  },
};

export default dataEntryApi;

