@app.put("/api/venues/{venue_id}")
def update_venue_capacity(venue_id: str, venue: VenueUpdate):
    """Update venue capacity"""
    try:
        # Check if venue exists
        venues = csv_manager.read_csv('venues')
        venue_exists = any(v['venue_id'] == venue_id for v in venues)

        if not venue_exists:
            raise HTTPException(status_code=404, detail="Venue not found")

        # Update venue capacity
        success = csv_manager.update_record('venues', venue_id, {
            'capacity': str(venue.capacity)
        })

        if not success:
            raise HTTPException(status_code=404, detail="Venue not found")

        # Reload data if it was already loaded
        if data_processor:
            load_csv_data()

        return {
            "message": "Venue capacity updated successfully",
            "venue_id": venue_id,
            "capacity": venue.capacity
        }
    except ValueError as e:
        raise HTTPException(status_code=400, detail=str(e))
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error updating venue: {str(e)}")

