@app.post("/api/sales/bulk")
def bulk_weekly_sales(bulk_sales: BulkWeeklySalesEntry):
    """Bulk create/update weekly sales entries"""
    try:
        results = []
        errors = []

        for i, entry in enumerate(bulk_sales.entries):
            try:
                # Validate entry format
                required_fields = ['concert_id', 'calendar_week', 'sales_week_number', 'tickets_sold_in_week', 'tickets_sold_total']
                for field in required_fields:
                    if field not in entry:
                        errors.append(f"Entry {i+1}: Missing required field '{field}'")
                        continue

                # Create or update entry
                sale_id = csv_manager.add_record('weekly_sales', {
                    'concert_id': entry['concert_id'],
                    'calendar_week': entry['calendar_week'],
                    'sales_week_number': str(entry['sales_week_number']),
                    'tickets_sold_in_week': str(entry['tickets_sold_in_week']),
                    'tickets_sold_total': str(entry['tickets_sold_total'])
                })

                results.append({
                    "entry_index": i + 1,
                    "sale_id": sale_id,
                    "status": "success"
                })

            except Exception as e:
                errors.append(f"Entry {i+1}: {str(e)}")

        return {
            "message": f"Bulk operation completed. {len(results)} successful, {len(errors)} errors",
            "successful_entries": len(results),
            "errors": errors,
            "results": results
        }
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error in bulk operation: {str(e)}")

def get_filtered_processor(artist: str):
    """Helper function to create a filtered processor for a specific artist."""
    if not original_csv_files:
        raise HTTPException(status_code=404, detail="No data available. CSV data not loaded.")

    # Create new BytesIO objects from the original data
    csv_files_copy = {}
    for name, original_stream in original_csv_files.items():
        original_stream.seek(0)  # Reset to beginning
        csv_files_copy[name] = io.BytesIO(original_stream.read())

    return DataProcessorV2(csv_files_copy, artist_filter=artist)

